export declare const EkycStyleHTML = "<style>\n.ekyct-container, .ekyct-container * {margin: 0;padding:0;box-sizing: border-box}\n.ekyct-container{position:fixed;top:0;left:0;background-color: #202124;z-index:99;height:100%;width:100%}\n.ekyct-container--inner {display: flex; flex-direction:column;width:100%;height:100%;position:absolute;top:0;left:0}\n.ekyct-container--rotate {flex-direction: row;}\n.ekyct-container--rotate .ekyct-header, .ekyct-container--rotate .ekyct-footer {\n    width: unset;\n    height: 100%;\n}\n.ekyct-container--rotate .ekyct-header--inner, .ekyct-container--rotate .ekyct-footer--inner {\n    width: unset;\n    height: 100%;\n    flex-direction: column-reverse;\n}\n\n.ekyct-btn {\n    border: none;\n    outline: none;\n    background-color: #3c4043;\n    color: #ffffff;\n    padding: 16px;\n    cursor: pointer;\n    line-height: 0;\n    border-radius: 50%\n}\n.ekyct-btn:hover {\n    background-color: #4b5053\n}\n.ekyct-btn:active {\n    background-color: #5a5f63\n}\n.ekyct-btn:disabled {\n    background-color: #2b2d30;\n    color: #727275;\n}\n.ekyct-btn svg {\n    fill: currentColor;\n    width: 28px;\n    height: 28px;\n}\n.ekyct-capture-region {\n    width: 100%;\n    min-height: 100px;\n    position: relative;\n    text-align: center;\n    flex-grow: 1;\n    display: flex;\n    align-items: center;\n    justify-content: center;\n}\n.ekyct-header, .ekyct-footer {\n    width: 100%;\n}\n.ekyct-header--inner {\n    width: 100%;\n    display: flex;\n    justify-content: flex-end;\n    padding: 10px;\n}\n\n.ekyct-header--inner .ekyct-btn { padding: 6px; }\n\n.ekyct-footer--inner {\n    width: 100%;\n    display: flex;\n    padding: 20px;\n    justify-content: center;\n    gap: 1rem;\n}\n\n.ekyct-cam-error {\n    width: 100%;\n    heigth: 100%;\n}\n\n.ekyct-cam-error--inner {\n    width: 100%;\n    height: 100%;\n    display: flex;\n    justify-content: center;\n    align-items: center;\n    flex-direction: column;\n}\n\n.ekyct-cam-error--inner span, .ekyct-cam-error--inner p {\n    color: #ff7011;\n    font-size: 14px;\n    text-align: center;\n}\n\n.ekyct-cam-error--inner svg {\n    fill: currentColor;\n    width: 96px;\n    height: 96px;\n}\n\n.ekyct-shading {\n    position: absolute;\n    border-style: solid;\n    border-color: rgba(0, 0, 0, 0.5);\n}\n\n.ekyct-shader-border {\n    position: absolute;\n    background-color: #ffffff;\n}\n\n.ekyct-video {\n    width: 100%;\n    display: block;\n}\n\n.ekyct-container--rotate .ekyct-video {\n    width: unset;\n    height: 100%;\n} \n\n/* .ekyct-container--rotate .ekyct-video {\n    max-width: calc(100vw - 360px)\n} */\n\n@media (min-width: 768px) {\n    .ekyct-container--inner {\n        max-width: 576px;\n        left: 50%;\n        transform: translateX(-50%)\n    }\n}\n\n@media (min-width: 992px) {\n    .ekyct-container--inner {\n        max-width: 768px;\n    }\n}\n\n</style>";
